/*
给定一个由 0 和 1 组成的矩阵，找出每个元素到最近的 0 的距离。

两个相邻元素间的距离为 1 。

示例 1:
输入:

0 0 0
0 1 0
0 0 0
输出:

0 0 0
0 1 0
0 0 0
示例 2:
输入:

0 0 0
0 1 0
1 1 1
输出:

0 0 0
0 1 0
1 2 1
注意:

给定矩阵的元素个数不超过 10000。
给定矩阵中至少有一个元素是 0。
矩阵中的元素只在四个方向上相邻: 上、下、左、右。
*/
#include <iostream>
#include <vector>
#include <queue>
using namespace std;
class Solution {
public:
	vector<vector<int>> directions = {{-1,0},{0,1},{1,0},{0,-1}};
    vector<vector<int>> updateMatrix(vector<vector<int>>& matrix) {
        vector<vector<int>> ans(matrix.size());
        for(int i = 0; i < matrix.size(); ++i ) {
        	ans[i].resize(matrix[0].size(),100);
        }
        for(int i = 0;i < matrix.size();++i) {
        	for(int j = 0; j < matrix[0].size(); ++j) {
                if(matrix[i][j] == 0) ans[i][j] = 0;
                if(ans[i][j] != 100) continue;
        		ans[i][j] = dfs(matrix, i ,j, ans, matrix.size(), matrix[0].size());
        	}
        }
        // dfs(matrix, 0,0, ans,matrix.size(), matrix[0].size());
        return ans;
    }
    //超时
    int dfs(vector<vector<int>>& matrix, int x, int y, vector<vector<int>> ans, int r, int c) {
    	if(matrix[x][y] == 0) {
            ans[x][y] = 0;
    		return 0;
    	}
        if(ans[x][y]!=100) {
            return ans[x][y];
        }
    	for(auto dir : directions ) {
    		if(x+dir[0] < 0 || x+dir[0] >= r || y+dir[1] < 0 || y+dir[1] >= c) {
    			continue;
    		}
            if((dir[0] == -1 || dir[1] == -1) && ans[x+dir[0]][y+dir[1]] == 100) continue;
    		int tmp = dfs(matrix, x+dir[0],y+dir[1], ans, r, c) + 1;
            if(tmp == 1) return 1;
    		ans[x][y] = min(ans[x][y], tmp);
    	}
    	return ans[x][y];
    }
    //选择0的元素入队,遍历队列元素，更新每个元素四个方向上的邻点的值
    vector<vector<int>> bfs(vector<vector<int>>& matrix) {
        queue<vector<int>> q;
        for(int i = 0; i < matrix.size();++i) {
            for(int j = 0; j < matrix[0].size(); ++j) {
                if(matrix[i][j] != 0) {
                    matrix[i][j] = 100;
                }else {
                    vector<int> tmp = {i,j};
                    q.push(vector<int>{i,j});
                }
            }
        }
        int rows = matrix.size();
        int cols = matrix[0].size();
        while(!q.empty()) {
            auto s = q.front();
            q.pop();
            for(auto d : directions) {
                int r = s[0]+d[0];
                int c = s[1] + d[1];
                if(r < 0 || r >= rows || c < 0 || c >= cols ) continue;
                if(matrix[r][c] > matrix[s[0]][s[1]]+1) {
                    matrix[r][c] = matrix[s[0]][s[1]]+1;
                    q.push(vector<int>{r,c});
                }
            }
        }
        return matrix;

    }
};

